<script>
  (function($){

      "use strict";

      var formEl = $("#subscribe");

      formEl.on('submit', (e)=>{
          e.preventDefault();
          var data = formEl.serialize();

          $.ajax({
          url:'https://script.viserlab.com/paylab/subscribe',
          method:'post',
          data:data,

          success:(response)=>{
              if(response.success){
                  formEl.find("input[name=email]").val('');
                  notify('success', response.message);
              }else{
                  $.each(response.error, ( key, value )=>{
                      notify('error', value);
                  });
              }
          },
          error:(error)=>{
              console.log(error);
          }

          });
      });

  })(jQuery);
</script>

